// we have to install @mui/style to get this to work, when the dropzone is available please remove this.
// @mui/style is not compatible with react 18 and it was depracated on mui 5.
import { DropzoneArea } from "react-mui-dropzone";
import { Alert, MenuItem, Grid, Typography } from "@mui/material";
import { makeValidate, showErrorOnBlur, makeRequired } from "mui-rff";
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import i18n from "i18next";
import { Form } from "react-final-form";
import { ContactFormInterface } from "../../interfaces/ContactFormInterface";
import {
  ContactFormBox,
  ContactTextField,
  SubmitButton,
  FileUpload,
  ContactFormHeader
} from "./ContactForm.style";
import { ContactFormSchma } from "../../schema/ContactFormSchema";
import { TabTitle } from "../../utils/GeneralFunctions";
import { useContactForm } from "../../providers/ContactFormProvider";
import Confirmation from "../../components/Confirmation/Confirmation";
import { ReactComponent as CloudUpload } from "../../assets/images/cloud-upload.svg";
import { useParams } from "react-router-dom";
import Compressor from 'compressorjs';

/*
 * Uses the optional helper makeValidate function to format the error messages
 * into something usable by final form.
 */
const validate = makeValidate(ContactFormSchma, error => {
  return <span className="error">{i18n.t(error.message)}</span>;
});

const required = makeRequired(ContactFormSchma);

const ContactForm = () => {
  const { t } = useTranslation();
  TabTitle(t("form-contact.meta.header.browser-tab-title"));
  const  { submitFormValues } = useContactForm();
  const [alertState, setAlertState] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [selectedCase, setSelectedCase] = useState("");
  const [submittedValues, setSubmittedValues] = useState<
    ContactFormInterface | undefined
  >(undefined);
  const [file, setFile] = useState([]);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [trackingValue, setTrackingValue] = useState("");
  const params = useParams();

  useEffect(() => {
    if (params && params.trackingId) {
      if (selectedCase !== "Portal - e-Tailer: Follow-up on an existing case") {
        setTrackingValue(params.trackingId);
      } else {
        setTrackingValue("");
      }
    }
  }, [params, trackingValue, selectedCase]);

  const handleTrackingChange = event => {
    setTrackingValue(event.target.value)
  }

  const handleIssueTypeChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setSelectedCase(event.target.value);
  };

  const initialValues: ContactFormInterface = {
    issueType: "",
    trackingNumber: "",
    description: ""
  };

  const onSubmit = async (values: ContactFormInterface) => {
    values.issueType = selectedCase;
    values.trackingNumber = trackingValue;
    setAlertState(false);
    setAlertMessage("");
    setSubmittedValues(values);
    const res: any = await submitFormValues(values, file);
    if (res?.success === undefined && res?.status.includes("ERROR")) {
      setShowConfirmation(false);
      setAlertState(true);
      setAlertMessage(res?.message);
    } else {
      setShowConfirmation(true);
    }
  };

  const handleFileChange = (files: any) => {
    if (files[0]) {
      if (files[0].type.includes("image/")) {
        new Compressor(files[0], {
          quality: 0.6,
          success: (res: any) => {
            setFile(res);
          }
        });
      } else {
        setFile(files[0]);
      }
    } else {
      setFile([]);
    }
  };

  const acceptedFileFormat = ["image/*",".pdf",".doc",".xls",".rtf",".docx",".docm",".xls",".xlsx",".xlsm",".zip",".ppt",".pptx",".pptm",".xml", ".csv"];

  const handleFileDrop = (files, rejectedFiles) => {};

  const issuesType = [
    {
      key: "Portal - e-Tailer: Label issues",
      value: t("form-contact.block-form.input.list.label-issue")
    },
    {
      key: "Portal - e-Tailer: Booking Issues",
      value: t("form-contact.block-form.input.list.booking-issues")
    },
    {
      key: "Portal - e-Tailer: Follow-up on an existing case",
      value: t("form-contact.block-form.input.list.follow-up")
    },
    {
      key: "Portal - e-Tailer: Tracking",
      value: t("form-contact.block-form.input.list.tracking")
    },
    {
      key: "Portal - e-Tailer: Technical Issue",
      value: t("form-contact.block-form.input.list.technical")
    },
    {
      key: "Portal - e-Tailer: General Feedback",
      value: t("form-contact.block-form.input.list.general-feedback")
    },
    {
      key: "Portal - e-Tailer: Address change/correction",
      value: t("form-contact.block-form.input.list.address-change")
    },
    {
      key: "Portal - e-Tailer: New claim",
      value: t("form-contact.block-form.input.list.open-claim")
    }
  ];

  const renderTrackingElement = () => {
    if (
      selectedCase === "Portal - e-Tailer: Label issues" ||
      selectedCase === "Portal - e-Tailer: General Feedback" ||
      selectedCase === "Portal - e-Tailer: Tracking" ||
      selectedCase === "Portal - e-Tailer: Technical Issue" ||
      selectedCase === "Portal - e-Tailer: Follow-up on an existing case" ||
      selectedCase === "Portal - e-Tailer: Address change/correction" ||
      selectedCase === "Portal - e-Tailer: New claim"
    ) {
      return (
        <ContactTextField
          label={
            selectedCase === "Portal - e-Tailer: Follow-up on an existing case"
              ? t("form-contact.block-form.input.list.case-number")
              : selectedCase === "Portal - e-Tailer: Label issues" ||
                selectedCase === "Portal - e-Tailer: Technical Issue" ||
                selectedCase === "Portal - e-Tailer: General Feedback"
              ? t("form-contact.block-form.input.list.tracking-number-optional")
              : t("form-contact.block-form.input.list.tracking-number")
          }
          id="trackingNumber"
          name="trackingNumber"
          variant="filled"
          autoComplete="name"
          value={trackingValue}
          onChange={handleTrackingChange}
          InputProps={{ disableUnderline: true }}
        />
      );
    }
    return null;
  };
  const renderDescriptionLabel = () => {
    switch (selectedCase) {
      case "Portal - e-Tailer: Label issues":
        return "form-contact.block-form.input.list.label-issue-description";
      case "Portal - e-Tailer: Booking Issues":
        return "form-contact.block-form.input.list.booking-issue-description";
      case "Portal - e-Tailer: Follow-up on an existing case":
        return "form-contact.block-form.input.list.follow-up-issue-description";
      case "Portal - e-Tailer: Tracking":
        return "form-contact.block-form.input.list.tracking-issue-description";
      case "Portal - e-Tailer: Technical Issue":
        return "form-contact.block-form.input.list.technical-issue-description";
      case "Portal - e-Tailer: General Feedback":
        return "form-contact.block-form.input.list.feedback-issue-description";
      case "Portal - e-Tailer: Address change/correction":
        return "form-contact.block-form.input.list.address-issue-description";
      case "Portal - e-Tailer: New claim":
        return "form-contact.block-form.input.list.open-claim-description";
      default:
        return "";
    }
  };

  return (
    <>
      {!showConfirmation && (
        <ContactFormBox>
          <Grid container spacing={10} sx={{ pt: 0 }}>
            <Grid item xs={12} sm={5}>
              <ContactFormHeader
                component="h1"
                variant="display3"
                color="fuchsiaAccessible.main"
              >
                {t("form-contact.block1.header.page-title")}
              </ContactFormHeader>
              <Typography component="p" variant="body1">
                {t("form-contact.block1.text.page-description")}
              </Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              {alertState && <Alert severity="error">{t(alertMessage)}</Alert>}
              <Form
                onSubmit={onSubmit}
                initialValues={
                  submittedValues ? submittedValues : initialValues
                }
                validate={validate}
                render={({ handleSubmit, invalid, submitting, pristine }) => (
                  <form onSubmit={handleSubmit} noValidate={true}>
                    <ContactTextField
                      name="issueType"
                      id="issueType"
                      label={t(
                        "form-contact.block-form.input.placeholder-reason"
                      )}
                      select
                      required={required.issueType}
                      value={selectedCase}
                      onChange={handleIssueTypeChange}
                      variant="filled"
                      InputProps={{ disableUnderline: true }}
                    >
                      {issuesType.map((item) => (
                        <MenuItem key={item.key} value={item.key}>
                          {item.value}
                        </MenuItem>
                      ))}
                    </ContactTextField>
                    <Grid>
                      {renderTrackingElement()}
                      {(selectedCase === "Portal - e-Tailer: Label issues" ||
                        selectedCase === "Portal - e-Tailer: Booking Issues" ||
                        selectedCase === "Portal - e-Tailer: General Feedback" ||
                        selectedCase === "Portal - e-Tailer: Tracking" ||
                        selectedCase === "Portal - e-Tailer: Technical Issue" ||
                        selectedCase === "Portal - e-Tailer: Follow-up on an existing case" ||
                        selectedCase === "Portal - e-Tailer: Address change/correction" ||
                        selectedCase === "Portal - e-Tailer: New claim") && (
                        <div>
                          <ContactTextField
                            label={t(renderDescriptionLabel())}
                            id="description"
                            name="description"
                            variant="filled"
                            required={required.description}
                            multiline
                            rows={5}
                            autoComplete="name"
                            InputProps={{ disableUnderline: true }}
                            showError={showErrorOnBlur}
                          />
                          <FileUpload item xs={12} sm={12}>
                            <Typography>
                              {selectedCase ===
                              "Portal - e-Tailer: Label issues"
                                ? t(
                                    "form-contact.block-form.input.list.upload-photo"
                                  )
                                : selectedCase ===
                                  "Portal - e-Tailer: Follow-up on an existing case"
                                ? t(
                                    "form-contact.block-form.input.list.follow-up-file"
                                  )
                                : selectedCase ===
                                  "Portal - e-Tailer: New claim"
                                ? t(
                                    "form-contact.block-form.input.list.upload-signed-file"
                                  )
                                : t(
                                    "form-contact.block-form.input.list.upload-screenshot"
                                  )}
                            </Typography>
                            <Typography component="p" variant="caption">
                              {t(
                                "form-contact.block-form.input.text.upload-subheading"
                              )}
                              <a href="mailto:service@rivolution.com">
                                service@rivolution.com.
                              </a>
                            </Typography>
                            <DropzoneArea
                              acceptedFiles={acceptedFileFormat}
                              onChange={handleFileChange}
                              onDrop={handleFileDrop}
                              filesLimit={1}
                              Icon={CloudUpload}
                              maxFileSize={6000000}
                              showFileNames
                              dropzoneText={t(
                                "form-contact.block-form.input.list.drag-file"
                              )}
                            />
                          </FileUpload>
                        </div>
                      )}
                    </Grid>
                    <SubmitButton
                      variant="contained"
                      type="submit"
                      size="large"
                      disabled={invalid}
                    >
                      {t("form-contact.block-form.button.submit")}
                    </SubmitButton>
                  </form>
                )}
              />
            </Grid>
          </Grid>
        </ContactFormBox>
      )}
      {showConfirmation && <Confirmation />}
    </>
  );
};

export default ContactForm;
