import React, { useEffect, useState } from "react";
import { getI18n, useTranslation } from "react-i18next";
import rivoLogo from "../../assets/images/rivo-logo.svg";
import hamburger from "../../assets/images/hamburger-icon.svg";
import LogoutIcon from "@mui/icons-material/Logout";
import CloseIcon from "@mui/icons-material/Close";
import { Link } from "react-router-dom";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";
import { FormControl, Collapse, Typography, Button } from "@mui/material";
import { useAuth } from "../../providers/Auth";
import { useCustomer } from "../../providers/CustomerProvider";
import {
  HeaderButton,
  MainContainerMenu,
  MainLeftNav,
  SideRightNavItem,
  MainLeftNavItemHref,
  MainLeftNavItemLi,
  MainLeftNavItemUL,
  MainRightNav,
  MainRightNavItem,
  SideNavContainer,
  SideRightNavItemUL,
  SideRightNavItemLi,
  BoldLine,
  SideNavFooterContainer,
  PopoverDiv,
  MenuHamburgerButton,
  LogoWrapper,
  GreetingDiv,
  MainContainerDiv,
  PopOverPaper,
  DrawerPaper,
  MainWrapper,
  SideNavFooterContent,
  FooterLinks,
  SideDrawer,
  SwitchButton
} from "../Header/Header.style";

function Header() {
  const user = useAuth();
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(
    null
  );
  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleMenuSignOut = e => {
    e.preventDefault();

    window.location.href.replace("login", "");
    user.signOut();
    toggleDrawer(false);
    handleClose();
  };

  const openMenu = Boolean(anchorEl);
  const id = openMenu ? "simple-popover" : undefined;

  const [sideNav, setSideNav] = React.useState(false);
  const toggleDrawer = open => event => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }
    setSideNav(open);
  };
  const { customerData } = useCustomer();

  // -------To get FirstName-------

  const fCapital = customerData[0]?.firstName as string;
  const firstName = fCapital?.charAt(0).toUpperCase() + fCapital?.slice(1);
  //-------- get LastName----------

  const lCapital = customerData[0]?.lastName as string;
  const lastName = lCapital?.charAt(0).toUpperCase() + lCapital?.slice(1);
  //Get FullName
  const userFullName = (firstName + " " + lastName) as string | undefined;
  let userInitial: string | undefined;
  if (typeof userFullName === "string") {
    const matches = userFullName.match(/\b(\w)/g);
    userInitial = matches?.join("");
  }
  const { t } = useTranslation();
  const initial = (t("all.header.button.btn-initial-profile-logged-in") +
    userFullName +
    t("all.header.button.btn-initial-profile-click-profile")) as
    | string
    | undefined;

  const { i18n } = useTranslation();

  //For Languge

  const [langValue, setLangValue] = useState(getI18n().resolvedLanguage);

  const onLangSwitch = e => {
    sessionStorage.setItem("language", e.target.checked ? "fr" : "en");
    setLangValue(e.target.checked ? "fr" : "en");
  };
  useEffect(() => {
    i18n.changeLanguage(langValue, (err, t) => {
      if (err) return console.error("ERROR: header had something went wrong loading", err);
      t("key");
    });
  }, [i18n, langValue]);

  //Set css class based on condition
  var languageclass,
    hamIconclass = "";
  if (user.user != null) {
    languageclass = "language-hide";
    hamIconclass = "hamIconShow";
  } else {
    languageclass = "language-show";
    hamIconclass = "hamIconHide";
  }
  return (
    <>
      <MainWrapper>
        <MainContainerDiv>
          <MainContainerMenu>
            {/* LeftSide menu item */}
            <MainLeftNav>
              <div className="logo-wrapper">
                <img src={rivoLogo} alt="Rivo" width="100%" height="100%" />
              </div>
            </MainLeftNav>
            {/* Rightside menu item */}
            <MainRightNav>
              <MainRightNavItem>
                <Collapse in={user.user !== null}>
                  <div className="main-left-nav-item">
                    <MainLeftNavItemUL>
                      <MainLeftNavItemLi>
                        <MainLeftNavItemHref href="./shipments-dashboard">
                          {" "}
                          {t("all.header.link.shipment-list")}
                        </MainLeftNavItemHref>
                      </MainLeftNavItemLi>
                      <MainLeftNavItemLi>
                        <MainLeftNavItemHref href="./contact">
                          {t("all.header.link.got-an-issue")}
                        </MainLeftNavItemHref>
                      </MainLeftNavItemLi>
                    </MainLeftNavItemUL>
                  </div>
                </Collapse>

                <Collapse in={user.user !== null}>
                  <GreetingDiv>
                    <HeaderButton
                      tabIndex={0}
                      aria-label={initial}
                      sx={{
                        "&:hover": {
                          backgroundColor: "White",
                          boxShadow: "0px 2px 2px 0px rgba(0,0,0,0.75)"
                        },
                        "&:focus-visible": {
                          backgroundColor: "White",
                          boxShadow: "0px 2px 2px 0px rgba(0,0,0,0.75)"
                        }
                      }}
                      color="secondary"
                      onClick={handleClick}
                    >
                      {userInitial}
                    </HeaderButton>
                  </GreetingDiv>
                  <PopoverDiv
                    id={id}
                    open={openMenu}
                    anchorEl={anchorEl}
                    onClose={handleClose}
                    anchorOrigin={{
                      vertical: "bottom",
                      horizontal: "left"
                    }}
                  >
                    <PopOverPaper>
                      <HeaderButton
                        sx={{ border: "1px solid #EBE7E6" }}
                        color="secondary"
                        aria-describedby={id}
                      >
                        {userInitial}
                      </HeaderButton>
                      <div>{userFullName}</div>
                    </PopOverPaper>
                    <BoldLine></BoldLine>
                    <MainLeftNavItemUL
                      sx={{ display: "flex", flexDirection: "column" }}
                    >
                      <MainLeftNavItemLi>
                        <Link
                          className="menu-action"
                          onClick={handleClose}
                          to="./view-profile"
                        >
                          <PersonOutlineIcon
                            sx={{ fontSize: "2rem" }}
                          ></PersonOutlineIcon>
                          <span className="menu-text" aria-label="profile">
                            {t("all.header.link.-profile")}
                          </span>
                        </Link>
                      </MainLeftNavItemLi>
                      <MainLeftNavItemLi
                        sx={{ paddingTop: "5px", paddingBottom: "0" }}
                      >
                        <Button
                          disableRipple
                          disableFocusRipple
                          variant="text"
                          className="menu-action"
                          onClick={handleMenuSignOut}
                        >
                          <LogoutIcon
                            sx={{
                              fontSize: "2rem",
                              transform: "rotate(180deg)"
                            }}
                          ></LogoutIcon>
                          <span className="menu-text">
                            {t("all.header-link.logout")}
                          </span>
                        </Button>
                      </MainLeftNavItemLi>
                    </MainLeftNavItemUL>
                  </PopoverDiv>
                </Collapse>
                <div className={languageclass}>
                  <FormControl>
                    <div>
                      <SwitchButton
                        aria-label={t("all.header.sr.language-menu")}
                        checked={langValue === "fr" ? true : false}
                        onChange={onLangSwitch}
                        tabIndex="0"
                      />
                    </div>
                  </FormControl>
                </div>
              </MainRightNavItem>
              <div className={hamIconclass}>
                <Collapse in={user.user !== null}>
                  <MenuHamburgerButton onClick={toggleDrawer(true)}>
                    <img src={hamburger} alt="" width="100%" height="100%" />
                  </MenuHamburgerButton>
                </Collapse>
              </div>
            </MainRightNav>
          </MainContainerMenu>
          {/* SideHamburger menu item(mobile and desktop) */}
          <Collapse in={user.user !== null}>
            <SideNavContainer>
              <SideDrawer
                anchor="right"
                open={sideNav}
                onClose={toggleDrawer(false)}
              >
                <DrawerPaper>
                  <LogoWrapper sx={{ paddingLeft: "1rem" }}>
                    <img src={rivoLogo} alt="Rivo" width="100%" height="100%" />
                  </LogoWrapper>
                  <CloseIcon
                    sx={{ fontSize: "2.5rem", paddingRight: "0px" }}
                    onClick={toggleDrawer(false)}
                  ></CloseIcon>
                </DrawerPaper>
                <SideRightNavItem>
                  <SideRightNavItemUL>
                    <SideRightNavItemLi>
                      <MainLeftNavItemHref href="./shipments-dashboard">
                        {t("all.header.link.shipment-list")}{" "}
                      </MainLeftNavItemHref>
                      <BoldLine></BoldLine>
                    </SideRightNavItemLi>
                    <SideRightNavItemLi>
                      <MainLeftNavItemHref href="./contact">
                        {" "}
                        {t("all.header.link.got-an-issue")}{" "}
                      </MainLeftNavItemHref>
                      <BoldLine></BoldLine>
                    </SideRightNavItemLi>
                    <SideRightNavItemLi>
                      <MainLeftNavItemHref href="./view-profile">
                        {" "}
                        {t("all.header.link.-profile")}{" "}
                      </MainLeftNavItemHref>
                      <BoldLine></BoldLine>
                    </SideRightNavItemLi>
                    <SideRightNavItemLi>
                      <Button
                        disableRipple
                        disableFocusRipple
                        variant="text"
                        className="side-menu-action"
                        onClick={handleMenuSignOut}
                      >
                        <span className="side-menu-text">
                          {t("all.header-link.logout")}
                        </span>
                      </Button>
                      <BoldLine></BoldLine>
                    </SideRightNavItemLi>
                  </SideRightNavItemUL>
                  <SideNavFooterContainer>
                    <SideNavFooterContent>
                      <FormControl sx={{ m: 1, width: "100%" }}>
                        <div className="side-lang">
                          <SwitchButton
                            aria-label={t("all.header.sr.language-menu")}
                            checked={langValue === "fr" ? true : false}
                            onChange={onLangSwitch}
                            tabIndex="0"
                          />
                        </div>
                      </FormControl>
                      <BoldLine> </BoldLine>
                      <FooterLinks>
                        <Typography
                          component="a"
                          color="black.main"
                          href={`${process.env.REACT_APP_SMARTKARGO_BASE_URL}privacy-policy`}
                          target="_blank"
                        >
                          {t("all.footer.link.privacy-policy")}
                        </Typography>
                        <Typography
                          component="a"
                          color="black.main"
                          href={`${process.env.REACT_APP_SMARTKARGO_BASE_URL}terms-of-use`}
                          target="_blank"
                        >
                          {t("all.footer.link.terms-of-use")}
                        </Typography>
                        <Typography
                          component="a"
                          color="black.main"
                          href={`${process.env.REACT_APP_SMARTKARGO_BASE_URL}cookie-policy`}
                          target="_blank"
                        >
                          {t("all.footer.link.cookies-policy")}
                        </Typography>
                      </FooterLinks>
                    </SideNavFooterContent>
                  </SideNavFooterContainer>
                </SideRightNavItem>
              </SideDrawer>
            </SideNavContainer>
          </Collapse>
        </MainContainerDiv>
      </MainWrapper>
    </>
  );
}
export default Header;
