import React, { useEffect, useState } from "react";
import { Outlet, Navigate } from "react-router-dom";
import { useAuth } from "../../providers/Auth";

const PrivateRoute = () => {
  const user = useAuth();
  const [state, setState] = useState({});
  useEffect(() => {
    const fetchState = async () => {
      await user.checkSessionExpired();
      console.log("ALERT MESAGE", sessionStorage.getItem("alertMessage"));
      setState(JSON.parse(sessionStorage.getItem("alertMessage") || "{}"));
    };
    fetchState();
  }, [user]);

  console.log("PRIVATE ROUTE STATE", JSON.stringify(state));

  if (user.loading) return <div>loading</div>;
  return user.user ? <Outlet /> : <Navigate to="/login" state={state} />;
};

export default PrivateRoute;
