import { styled, alpha, lighten } from "@mui/material/styles";
import { Accordion } from "@mui/material";
import TabList from "@mui/lab/TabList";
import TabPanel from "@mui/lab/TabPanel";

export const RowSummaryWrapper = styled("div")(({ theme }) => ({
  display: "flex",
  flexFlow: "row wrap",
  width: "100%",
  [theme.breakpoints.up("md")]: {
    flexFlow: "row nowrap"
  },
  "& .data-label": {
    fontSize: "1.2rem",
    lineHeight: "2rem",
    whiteSpace: "nowrap"
  },
  "& .data-value": {
    fontSize: "1.3rem",
    fontWeight: "700",
    lineHeight: "2rem",
    textOverflow: "ellipsis",
    overflow: "hidden",
    whiteSpace: "nowrap"
  },
  "&  .data-extra": {
    display: "flex",
    alignItems: "center",
    fontWeight: "400",
    textTransform: "capitalize",
    "& .MuiSvgIcon-root": {
      fontSize: "1.3rem",
      color: "inherit",
      margin: "0 0.5rem"
    }
  }
}));

export const RowSummaryAirbill = styled("div")(({ theme }) => ({
  flex: "0 0 50%",
  paddingBottom: "1rem",
  paddingRight: "1rem",
  maxWidth: "50%",
  [theme.breakpoints.up("sm")]: {
    flex: "0 0 25%",
    maxWidth: "25%"
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 16%",
    paddingRight: "2rem",
    maxWidth: "16%",
    order: 1
  }
}));

export const RowSummaryReference = styled("div")(({ theme }) => ({
  flex: "0 0 50%",
  paddingBottom: "1rem",
  paddingRight: "2.5rem",
  maxWidth: "50%",
  [theme.breakpoints.up("sm")]: {
    flex: "0 0 25%",
    maxWidth: "25%",
    paddingRight: "1rem"
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 16%",
    paddingRight: "2rem",
    maxWidth: "16%",
    order: 1
  }
}));

export const RowSummaryConsigneeName = styled("div")(({ theme }) => ({
  flex: "0 0 50%",
  paddingBottom: "1rem",
  paddingRight: "1rem",
  maxWidth: "50%",
  [theme.breakpoints.up("sm")]: {
    flex: "0 0 25%",
    maxWidth: "25%"
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 16%",
    paddingRight: "2rem",
    maxWidth: "16%",
    order: 1
  }
}));

export const RowSummaryEstimatedDeliveryDate = styled("div")(({ theme }) => ({
  flex: "0 0 50%",
  paddingBottom: "1rem",
  paddingRight: "1rem",
  maxWidth: "50%",
  "& .data-value": {
    textTransform: "capitalize"
  },
  [theme.breakpoints.up("sm")]: {
    flex: "0 0 25%",
    maxWidth: "25%",
    paddingRight: "2.5rem"
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 19%",
    maxWidth: "19%",
    order: 2
  }
}));

export const RowSummaryLastEventType = styled("div")(({ theme }) => ({
  flex: "0 0 100%",
  paddingBottom: "1rem",
  maxWidth: "100%",
  "& .data-value": {
    display: "flex",
    justifyContent: "space-between"
  },
  "&  .data-bar": {
    backgroundColor: theme.palette.disabled.main,
    width: "100%",
    height: "0.5rem",
    position: "relative",
    borderRadius: "2.5rem"
  },
  "&  .data-progression": {
    backgroundColor: theme.palette.success.main,
    width: "0",
    height: "0.5rem",
    position: "absolute",
    borderRadius: "2.5rem",
    "&[data-type='BKD']": {
      width: "10%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='RCS']": {
      width: "30%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='DEP']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='RCF']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='RPU']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='MAN']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='ARR']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='PUL']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='DLV']": {
      width: "60%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='GDL']": {
      width: "80%",
      backgroundColor: theme.palette.success.main
    },
    "&[data-type='DLD']": {
      width: "100%",
      backgroundColor: theme.palette.black.main
    },
    "&[data-type='DPU']": {
      width: "100%",
      backgroundColor: theme.palette.black.main
    },
    "&[data-type='DDL']": {
      width: "100%",
      backgroundColor: theme.palette.black.main
    },
    "&[data-type='ADL']": {
      width: "80%",
      backgroundColor: theme.palette.ochre.main
    },
    "&[data-type='RTS']": {
      width: "100%",
      backgroundColor: theme.palette.error.main
    }
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 34%",
    paddingRight: "2rem",
    maxWidth: "34%",
    order: 1
  }
}));

export const RowAccordion = styled(Accordion)(({ theme }) => ({
  backgroundColor: theme.palette.white.main,
  borderRadius: "0.8rem",
  [theme.breakpoints.up("md")]: {
    borderRadius: 0,
    marginBottom: 0
  },
  "&:first-of-type": {
    "& .MuiAccordionSummary-root": {
      [theme.breakpoints.up("md")]: {
        borderRadius: "0.8rem 0.8rem 0 0"
      }
    }
  },
  "&:last-of-type": {
    marginBottom: 0,
    "& .MuiAccordionSummary-root": {
      [theme.breakpoints.up("md")]: {
        borderRadius: "0 0 0.8rem 0.8rem",
        marginBottom: theme.spacer_m
      }
    }
  },
  "&:before": {
    backgroundColor: "transparent",
    [theme.breakpoints.up("md")]: {
      backgroundColor: theme.palette.disabled.main,
      margin: "0 1.6rem"
    }
  },
  "&.Mui-expanded": {
    [theme.breakpoints.up("md")]: {
      margin: 0
    }
  },
  "&:last-of-type, &:first-of-type": {
    borderRadius: "0.8rem"
  },
  "& .MuiAccordionSummary-root": {
    padding: "0 1.6rem",
    minHeight: "5.5rem",
    alignItems: "flex-start",
    [theme.breakpoints.up("md")]: {
      backgroundColor: theme.palette.white.main
    },
    "&.Mui-focusVisible": {
      backgroundColor: theme.palette.fuchsiaLight.main
    }
  },
  "& .MuiAccordionSummary-content": {
    margin: "1.6rem 0 1rem",
    [theme.breakpoints.up("md")]: {
      margin: "0.5rem 0 0.2rem"
    }
  },
  "& .MuiAccordionSummary-expandIconWrapper": {
    backgroundColor: theme.palette.fuchsiaLight.main,
    top: "1.6rem",
    right: "1.6rem",
    position: "absolute",
    [theme.breakpoints.up("md")]: {
      top: "50%",
      marginTop: "-1.2rem"
    }
  },
  "& .MuiSvgIcon-root": {
    fontSize: "2.4rem",
    color: theme.palette.fuchsiaAccessible.main
  },
  "& .MuiAccordionDetails-root": {
    padding: "0 1.6rem 2rem",
    [theme.breakpoints.up("sm")]: {
      paddingBottom: "2.4rem"
    }
  }
}));

export const ShipmentDetailsNavTabs = styled(TabList)(({ theme }) => ({
  marginLeft: "-1.6rem",
  width: "calc(100% + 3.2rem)",
  "& .MuiTabs-flexContainer": {
    borderBottom: `2px solid ${theme.palette.fuchsiaLight.main}`
  },
  "& .MuiTab-root": {
    fontSize: "1.3rem",
    textTransform: "none",
    "&.Mui-selected": {
      color: theme.palette.black.main,
      fontWeight: 700
    },
    "& .MuiTouchRipple-child": {
      backgroundColor: theme.palette.fuchsia.main
    },
    "&.Mui-disabled": {
      backgroundColor: alpha(theme.palette.disabled.main, 0.2)
    },
    "&:hover": {
      backgroundColor: theme.palette.fuchsiaLight.main
    },
    "&.Mui-focusVisible": {
      backgroundColor: theme.palette.fuchsiaLight.main
    }
  }
}));

export const ListTabPanel = styled(TabPanel)(() => ({
  padding: "2.4rem 0 1.6rem 0.2rem"
}));

export const ContentLoading = styled("div")(({ theme }) => ({
  textAlign: "center",
  padding: "1.6rem",
  "& .MuiCircularProgress-root": {
    color: lighten(theme.palette.fuchsia.main, 0.5)
  }
}));

export const DesktopTabWrapper = styled("div")(({ theme }) => ({
  [theme.breakpoints.up("sm")]: {
    display: "flex",
    flexFlow: "row nowrap",
    alignItems: "flex-start"
  },
  [theme.breakpoints.up("md")]: {
    border: `2px solid ${theme.palette.blueLight.main}`,
    borderRadius: "0.8rem",
    padding: "2rem"
  }
}));
