import { Box, Grid, Typography } from "@mui/material";
import { TextField } from "mui-rff";
import { styled } from "@mui/material/styles";
import { LoadingButton } from "@mui/lab";

export const ContactFormBox = styled((props: any) => <Box {...props} />)(
  ({ theme }) => ({
    marginLeft: theme.spacer_n,
    marginRight: theme.spacer_n,
    [theme.breakpoints.up("sm")]: {
      marginLeft: 0,
      marginRight: 0
    }
  })
);

export const ContactFormHeader = styled((props: any) => (
  <Typography {...props} />
))(({ theme }) => ({
  marginBottom: theme.spacer_m
}));

export const FileUpload = styled((props: any) => <Grid {...props} />)(
  ({ theme }) => ({
    backgroundColor: "white",
    borderRadius: "0.4rem",
    display: "flex",
    flexDirection: "column",
    padding: "2.6rem",
    marginBottom: "2rem",
    "& .MuiDropzoneArea-textContainer": {
      textAlign: "center",
      display: "flex",
      flexDirection: "column-reverse",
      alignItems: "center",
      marginTop: "2rem"
    },
    "& .MuiTypography-body1": {
      marginBottom: "0.7rem",
      fontWeight: "bold"
    },
    "& .MuiDropzoneArea-root": {
      backgroundColor: theme.palette.violetLight.main,
      borderColor: theme.palette.purple.main,
      borderRadius: "1rem",
      borderWidth: "0.2rem",
      minHeight: "19rem",
      padding: "1rem",
      "& .MuiTypography-h5": {
        margin: "1rem 0",
        whiteSpace: "pre-wrap",
        fontSize: "1.4rem",
        fontWeight: "bold"
      },
      "& .MuiSvgIcon-root": {
        color: theme.palette.common.fushiaAccessible,
        marginTop: "1rem"
      }
    },
    "& .MuiTypography-caption": {
      marginBottom: "1.8rem"
    }
  })
);

export const SubmitButton = styled((props: any) => <LoadingButton {...props} />)(
  ({ theme }) => ({
    "& .MuiInputLabel-root": {
      paddingLeft: "1.2rem"
    },
    float: "right",
    margin: "1rem",
    padding: "1rem",
    width: "100%",
    backgroundColor: theme.palette.common.fushiaAccessible,
    borderRadius: "2.7rem",
    maxWidth: "13rem",
    fontSize: "1em",
    textTransform: "capitalize",
    "&:hover": {
      backgroundColor: theme.palette.common.fushiaAccessible
    }
  })
);

export const ContactTextField = styled((props: any) => (
  <TextField {...props} />
))(({ theme }) => ({
  "& .MuiInputLabel-root": {
    paddingLeft: "1.7rem",
    whiteSpace: "normal",
    color: theme.palette.black.main,
    fontWeight: "500"
  },
  "&.textarea-address": {
    "& .MuiFilledInput-root": {
      paddingTop: "7rem"
    }
  },
  "&.textarea-medium": {
    "& .MuiFilledInput-root": {
      paddingTop: "4rem"
    }
  },
  "& .MuiInputLabel-root.Mui-focused": {
    minHeight: "35rem",
    color: theme.palette.black.main,
  },
  "& .MuiFilledInput-root": {
    overflow: "hidden",
    borderRadius: "0.4rem",
    backgroundColor: theme.palette.common.white,
    marginBottom: "1em",
    "& input.MuiFilledInput-input": {
      padding: "2.5rem 2.4rem  0.8rem",
      color: "black",
      fontWeight: "bold"
    },
    input: {
      "&:-webkit-autofill": {
        WebkitBoxShadow: "0 0 0 1000px white inset"
      }
    },
    "& textarea.MuiFilledInput-input": {
      paddingLeft: "1.3rem",
      color: "black",
      fontWeight: "bold"
    },
    textarea: {
      "&:-webkit-autofill": {
        WebkitBoxShadow: "0 0 0 1000px white inset"
      }
    }
  },
  "& .MuiFormHelperText-root.Mui-error": {
    padding: "1.2rem",
    fontSize: "1.3rem",
    borderRadius: "0.8rem",
    color: theme.palette.white.main,
    backgroundColor: theme.palette.fuchsiaAccessible.main,
    marginBottom: "1em",
    position: "relative",
    span: {
      display: "block"
    },
    "&:before": {
      content: '""',
      width: 0,
      height: 0,
      position: "absolute",
      borderLeft: "1rem solid transparent",
      borderRight: "1rem solid transparent",
      borderBottom: `1rem solid ${theme.palette.fuchsiaAccessible.main}`,
      top: "-1rem",
      marginLeft: "4rem"
    }
  },
  "& .MuiSelect-select.MuiInputBase-input.MuiFilledInput-input.MuiSelect-select": {
    paddingLeft: "2.6rem",
    color: "black",
    fontWeight: "bold"
  },
  "& .MuiSvgIcon-root.MuiSelect-icon": {
    marginRight: "1.6rem",
    fontSize: "2.4rem",
    [theme.breakpoints.down("sm")]: {
      marginRight: "0.16rem"
    }
  }
}));
