import { styled } from "@mui/material/styles";

export const DatePickerStyle = styled("div")(({ theme }) => ({
  flex: "0 0 100%",
  [theme.breakpoints.up("sm")]: {
    flex: "0 0 calc(100% * 2/3 - 1rem * 2/3)"
  },
  [theme.breakpoints.up("md")]: {
    flex: "0 0 35rem",
    maxWidth: "35rem"
  },
  "& .date-range-mobile": {
    [theme.breakpoints.up("md")]: {
      display: "none"
    }
  },
  "& .date-range-desktop": {
    display: "none",
    [theme.breakpoints.up("md")]: {
      display: "block"
    }
  },
  "& .MuiFormControl-root": {
    "&:first-of-type": {
      marginRight: "1rem"
    }
  }
}));
